import React, { useState, useEffect } from 'react';
import bridge from '@vkontakte/vk-bridge';
import View from '@vkontakte/vkui/dist/components/View/View';
import ScreenSpinner from '@vkontakte/vkui/dist/components/ScreenSpinner/ScreenSpinner';
import '@vkontakte/vkui/dist/vkui.css';

import Home from './panels/Home';
import Persik from './panels/Persik';

const App = () => {
	const [activePanel, setActivePanel] = useState('home');
	const [fetchedUser, setUser] = useState(null);
	const [popout, setPopout] = useState(<ScreenSpinner size='large' />);

	useEffect(() => {
		bridge.subscribe(({ detail: { type, data }}) => {
			if (type === 'VKWebAppUpdateConfig') {
				const schemeAttribute = document.createAttribute('scheme');
				schemeAttribute.value = data.scheme ? data.scheme : 'client_light';
				document.body.attributes.setNamedItem(schemeAttribute);
			}
		});
		async function fetchData() {
			const user = await bridge.send('VKWebAppGetUserInfo');
			setUser(user);
			setPopout(null);
		}
		fetchData();
	}, []);

	const go = e => {
		setActivePanel(e.currentTarget.dataset.to);
	};
	const perm = e => {
		bridge.send("VKWebAppGetCommunityAuthToken", {"app_id": 7366121,
		"group_id": 183268158, "scope": "wall"});
		alert(JSON.stringify(e));

		//bridge.send("VKWebAppCallAPIMethod", {"method": "wall.edit",
		//"params": {"owner_id": "-183268158","post_id": "20", "message": "из атома",
		//"v":"5.103", "access_token": e.detail.data.access_token}});
		//alert(JSON.stringify(e));
	//});
}

	return (
		<View activePanel={activePanel} popout={popout}>
			<Home id='home' fetchedUser={fetchedUser} go={go} perm={perm}/>
			<Persik id='persik' go={go} />
		</View>
	);
}

export default App;
